  height: 100vh;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.8);
  display: none;
  z-index: 100;
}
.overlay .popup {
  position: absolute;
  width: 600px;
  height: 368px;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  padding: 64px 80px;
  box-sizing: border-box;
  background: #FFFFFF;
  border-radius: 16px;
}
.overlay .popup h2 {
  font-weight: 500;
  font-size: 32px;
  line-height: 40px;
  color: #000000;
  margin-bottom: 16px;
}
.overlay .popup p {
  font-weight: 500;
  font-size: 18px;
  line-height: 32px;
  color: #6F767E;
}
.overlay .popup button {
  width: 106px;
  height: 56px;
  font-weight: 500;
  font-size: 18px;
  line-height: 32px;
  margin-top: 44px;
}
.overlay .close-popup {
  position: absolute;
  top: 15px;
  right: 15px;
  width: 23px;
  height: 23px;
  cursor: pointer;
}
.overlay .close-popup:before {
  content: "";
  background-color: #6F767E;
  position: absolute;
  height: 1px;
  width: 13px;
  top: 11px;
  left: -4px;
  transform: rotate(-45deg);
}
.overlay .close-popup:after {
  content: "";
  background-color: #6F767E;
  position: absolute;
  height: 1px;
  width: 13px;
  top: 11px;
  transform: rotate(45deg);
  left: -4px;
}

